css:
.markdown-preview {
  contain: paint;
}
.markdown-preview atom-text-editor + pre {
  display: none;
}
.markdown-preview atom-text-editor {
  user-select: auto;
}
.scrollbars-visible-always .markdown-preview atom-text-editor .vertical-scrollbar,
.scrollbars-visible-always .markdown-preview atom-text-editor .horizontal-scrollbar {
  visibility: hidden;
}
.scrollbars-visible-always .markdown-preview atom-text-editor:hover .vertical-scrollbar,
.scrollbars-visible-always .markdown-preview atom-text-editor:hover .horizontal-scrollbar {
  visibility: visible;
}
.markdown-preview .task-list-item input[type=checkbox] {
  position: absolute;
  margin: 0.25em 0 0 -1.4em;
}
.markdown-preview .task-list-item {
  list-style-type: none;
}
.markdown-preview.loading {
  display: flex;
  flex-direction: column;
  justify-content: center;
}
.markdown-preview.loading:before {
  display: block;
  content: 'Loading Markdownâ€¦';
  margin: auto;
  background-image: url(images/octocat-spinner-128.gif);
  background-repeat: no-repeat;
  background-size: 64px;
  background-position: top center;
  padding-top: 70px;
  text-align: center;
  opacity: 0;
  animation-duration: 1s;
  animation-name: appear-after-short-delay;
  animation-delay: 0.75s;
  animation-fill-mode: forwards;
}
@keyframes appear-after-short-delay {
  0% {
    opacity: 1;
  }
  100% {
    opacity: 1;
  }
}
