css:
.about {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  -webkit-user-select: none;
  cursor: default;
  overflow: auto;
  text-align: center;
  font-size: 1.25em;
  line-height: 1.4;
  padding: 4em;
  color: hsl(220, 13%, 66%);
  background-color: hsl(220, 13%, 18%);
}
.about button {
  cursor: default;
}
.about a:focus {
  color: inherit;
}
.about img,
.about a {
  -webkit-user-drag: none;
}
.about .input-checkbox {
  margin-top: -0.2em;
}
.about .min-width-min-content {
  min-width: min-content;
}
.about .group-start {
  margin-top: 4em;
}
.about .group-item {
  margin-top: 1.5em;
}
.about-container {
  width: 100%;
  max-width: 500px;
}
.about-container .icon::before {
  margin-right: 0;
}
.about-atom-io:hover .about-logo {
  color: #40a977;
}
.about-logo {
  display: block;
  width: 100%;
  max-width: 280px;
  margin: 0 auto 1em auto;
  color: hsl(220, 13%, 86%);
  transition: color 0.2s;
}
.about-header-info {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  column-gap: 30px;
}
.about-version-container {
  vertical-align: middle;
  white-space: nowrap;
}
.about-version-container:hover {
  color: hsl(220, 13%, 81%);
}
.about-version-container:active {
  color: hsl(220, 13%, 96%);
}
.about-version {
  margin-right: 0.5em;
  font-size: 1.25em;
  vertical-align: middle;
}
.about-more-version {
  color: hsla(220, 13%, 66%, 0.6);
  font-size: 0.9em;
}
.about-updates {
  width: 100%;
  max-width: 39em;
}
.about-updates-box {
  display: flex;
  align-items: center;
  padding: 10px;
  border: 1px solid hsl(220, 13%, 10.8%);
  border-radius: 6px;
  background-color: hsl(220, 13%, 22%);
}
.about-updates-status {
  flex: 1;
  margin-left: 0.5em;
  text-align: center;
}
.about-updates-item,
.about-default-update-message .about-updates-label {
  display: block;
}
.about-updates-label {
  color: hsla(220, 13%, 66%, 0.6);
}
.about-updates-label.is-strong {
  color: hsl(220, 13%, 66%);
}
.about-updates-version {
  margin: 0 0.4em;
}
.about-auto-updates {
  margin-top: 1em;
}
.about-auto-updates input {
  margin-right: 0.5em;
}
.about-love {
  min-width: max-content;
}
.about-love .icon::before {
  position: relative;
  width: auto;
  height: auto;
  margin-right: 0;
  font-size: 1.5em;
  vertical-align: text-top;
}
.about-love .icon-logo-github::before {
  font-size: 3.6em;
  height: 0.36em;
}
.about-credits {
  color: hsla(220, 13%, 66%, 0.6);
}
.about-release-notes {
  color: hsl(208, 88%, 48%);
}
.about-release-notes:hover {
  color: hsl(208, 88%, 63%);
}
