css:
.github-CommitDetailView {
  display: flex;
  flex-direction: column;
  height: 100%;
}
.github-CommitDetailView-header {
  flex: 0;
  border-bottom: 1px solid hsl(230, 1%, 86%);
  background-color: hsl(230, 1%, 98%);
}
.github-CommitDetailView-commit {
  padding: 20px;
  padding-bottom: 0;
}
.github-CommitDetailView-title {
  margin: 0 0 0.25em 0;
  font-size: 1.4em;
  line-height: 1.3;
  color: hsl(230, 1%, 14%);
}
.github-CommitDetailView-avatar {
  border-radius: 3px;
  height: 16px;
  margin-right: 0.4em;
  width: 16px;
}
.github-CommitDetailView-meta {
  display: flex;
  align-items: center;
  margin: 5px 0 20px 0;
}
.github-CommitDetailView-metaText {
  flex: 1;
  margin-left: 22.4px;
  line-height: 16px;
  color: hsl(230, 1%, 56%);
}
.github-CommitDetailView-moreButton {
  position: absolute;
  left: 50%;
  transform: translate(-50%, -50%);
  padding: 0em 0.4em;
  color: hsl(230, 1%, 56%);
  font-style: italic;
  border: 1px solid hsl(230, 1%, 86%);
  border-radius: 3px;
  background-color: hsl(0, 0%, 100%);
}
.github-CommitDetailView-moreButton:hover {
  background-color: hsl(0, 0%, 96%);
}
.github-CommitDetailView-moreText {
  padding: 20px 0;
  font-size: inherit;
  font-family: var(--editor-font-family);
  word-wrap: initial;
  word-break: break-word;
  white-space: pre-wrap;
  border-top: 1px solid hsl(230, 1%, 86%);
  background-color: transparent;
  max-height: 55vh;
}
.github-CommitDetailView-moreText:empty {
  display: none;
}
.github-CommitDetailView-sha {
  flex: 0 0 7ch;
  margin-left: 20px;
  line-height: 16px;
  color: hsl(230, 1%, 56%);
  font-family: var(--editor-font-family);
  white-space: nowrap;
  overflow: hidden;
}
.github-CommitDetailView-sha a {
  color: hsl(208, 100%, 54%);
}
