css:
atom-text-editor .find-result .region {
  background-color: transparent;
  border-radius: 3px;
  border: 1px solid hsla(230, 100%, 66%, 0.2);
  box-sizing: border-box;
  z-index: 0;
}
atom-text-editor .current-result .region {
  border-radius: 3px;
  border: 1px solid hsl(230, 100%, 66%);
  box-sizing: border-box;
  z-index: 0;
}
atom-text-editor .find-result,
atom-text-editor .current-result {
  display: none;
}
atom-workspace.find-visible atom-text-editor .find-result,
atom-workspace.find-visible atom-text-editor .current-result {
  display: block;
}
.find-and-replace,
.preview-pane,
.project-find {
  -webkit-user-select: none;
  padding: 5px;
  overflow-x: auto;
}
.find-and-replace .header,
.preview-pane .header,
.project-find .header {
  padding: 2.5px 5px;
  min-width: 200px;
  line-height: 1.75;
}
.find-and-replace .header-item,
.preview-pane .header-item,
.project-find .header-item {
  margin: 2.5px 0;
}
.find-and-replace .input-block,
.preview-pane .input-block,
.project-find .input-block {
  display: flex;
  flex-wrap: wrap;
  width: 100%;
  min-width: 200px;
}
.find-and-replace .input-block-item,
.preview-pane .input-block-item,
.project-find .input-block-item {
  display: flex;
  flex: 1;
  padding: 5px;
}
.find-and-replace .btn-group,
.preview-pane .btn-group,
.project-find .btn-group {
  display: flex;
  flex: 1;
}
.find-and-replace .btn-group .btn,
.preview-pane .btn-group .btn,
.project-find .btn-group .btn {
  flex: 1;
}
.find-and-replace .btn-group + .btn-group,
.preview-pane .btn-group + .btn-group,
.project-find .btn-group + .btn-group {
  margin-left: 10px;
}
.find-and-replace .btn > .icon,
.preview-pane .btn > .icon,
.project-find .btn > .icon {
  width: 20px;
  height: 16px;
  vertical-align: middle;
  fill: currentColor;
  stroke: currentColor;
  pointer-events: none;
}
.find-and-replace .close-button,
.preview-pane .close-button,
.project-find .close-button {
  margin-left: 10px;
  cursor: pointer;
  color: hsl(230, 1%, 56%);
}
.find-and-replace .close-button:hover,
.preview-pane .close-button:hover,
.project-find .close-button:hover {
  color: hsl(230, 1%, 14%);
}
.find-and-replace .close-button .icon::before,
.preview-pane .close-button .icon::before,
.project-find .close-button .icon::before {
  margin-right: 0;
  text-align: center;
  vertical-align: middle;
}
.find-and-replace .description,
.preview-pane .description,
.project-find .description {
  display: inline-block;
}
.find-and-replace .description .subtle-info-message,
.preview-pane .description .subtle-info-message,
.project-find .description .subtle-info-message {
  padding-left: 5px;
  color: hsl(230, 1%, 56%);
}
.find-and-replace .description .subtle-info-message .highlight,
.preview-pane .description .subtle-info-message .highlight,
.project-find .description .subtle-info-message .highlight {
  color: hsl(230, 1%, 26%);
  font-weight: normal;
}
.find-and-replace .options-label,
.preview-pane .options-label,
.project-find .options-label {
  color: hsl(230, 1%, 56%);
  position: relative;
}
.find-and-replace .options-label .options,
.preview-pane .options-label .options,
.project-find .options-label .options {
  margin-right: 0.5em;
  color: hsl(230, 1%, 26%);
}
.find-and-replace .btn-group-options,
.preview-pane .btn-group-options,
.project-find .btn-group-options {
  display: inline-flex;
  margin-top: -0.1em;
}
.find-and-replace .btn-group-options .btn,
.preview-pane .btn-group-options .btn,
.project-find .btn-group-options .btn {
  width: 36px;
  padding: 0;
  line-height: 1.75;
}
.find-and-replace .editor-container,
.preview-pane .editor-container,
.project-find .editor-container {
  position: relative;
}
.find-and-replace .editor-container atom-text-editor,
.preview-pane .editor-container atom-text-editor,
.project-find .editor-container atom-text-editor {
  width: 100%;
}
.find-and-replace .input-block-item {
  flex: 1 1 260px;
}
.find-and-replace .input-block-item--flex {
  flex: 100 1 260px;
}
.find-and-replace .btn-group-find,
.find-and-replace .btn-group-replace {
  flex: 1;
}
.find-and-replace .btn-group-find-all,
.find-and-replace .btn-group-replace-all {
  flex: 2;
}
.find-and-replace .find-container atom-text-editor {
  padding-right: 64px;
}
.find-and-replace .find-meta-container {
  position: absolute;
  top: 1px;
  right: 0;
  margin: 5px 5px 0 0;
  z-index: 2;
  font-size: 0.9em;
  line-height: 25px;
  pointer-events: none;
}
.find-and-replace .find-meta-container .result-counter {
  margin-right: 10px;
}
.find-wrap-icon {
  opacity: 0;
  transition: opacity 0.5s;
  position: absolute;
  top: 50% !important;
  left: 50% !important;
  right: initial !important;
  bottom: initial !important;
  margin-top: -60px;
  margin-left: -60px;
  background: hsla(230, 1%, 94%, 0.45);
  border-radius: 6px;
  text-align: center;
  pointer-events: none;
}
.find-wrap-icon.visible {
  opacity: 1;
}
.find-wrap-icon:before {
  font-size: 80px;
  line-height: 120px;
  height: 120px;
  width: 120px;
  color: hsl(230, 8%, 24%);
  opacity: 0.5;
}
.project-find .input-block-item {
  flex: 1 1 160px;
}
.project-find .input-block-item--flex {
  flex: 100 1 260px;
}
.project-find .loading,
.project-find .preview-block,
.project-find .error-messages,
.project-find .filter-container {
  display: none;
}
.preview-pane {
  position: relative;
  display: flex;
  flex-direction: column;
  padding: 0;
}
.preview-pane .preview-header {
  display: flex;
  flex-wrap: wrap;
  padding: 5px;
  align-items: center;
  justify-content: space-between;
  overflow: hidden;
  font-weight: normal;
  border-bottom: 1px solid hsl(230, 1%, 86%);
  background-color: hsl(230, 1%, 98%);
}
.preview-pane .preview-count {
  margin: 5px;
}
.preview-pane .preview-controls {
  display: flex;
  flex-wrap: wrap;
}
.preview-pane .preview-controls .btn-group {
  margin: 5px;
}
.preview-pane .loading-spinner-tiny,
.preview-pane .loading-spinner-tiny + .inline-block {
  vertical-align: middle;
}
.preview-pane .no-results-overlay {
  visibility: hidden;
}
.preview-pane.no-results .no-results-overlay {
  visibility: visible;
}
.preview-pane .results-view {
  overflow: auto;
  position: relative;
  flex: 1;
}
.preview-pane .results-view-container {
  padding-bottom: 10px;
}
.preview-pane .results-view .list-item {
  padding: 0 0 0 10px;
}
.preview-pane .results-view .context-row,
.preview-pane .results-view .match-row {
  padding: 0 0 0 10px;
  margin-left: 8px;
  box-shadow: inset 0 1px 0 #eaeaeb;
}
.preview-pane .results-view .context-row.separator,
.preview-pane .results-view .match-row.separator {
  box-shadow: inset 0 1px 0 hsl(230, 1%, 86%);
}
.preview-pane .results-view .line-number {
  margin-right: 1ex;
  text-align: right;
  display: inline-block;
}
.preview-pane .results-view .match-row.selected .line-number {
  color: hsl(230, 1%, 2%);
}
.preview-pane .results-view .path-match-number {
  padding-left: 10px;
  color: hsl(230, 1%, 56%);
}
.preview-pane .results-view .preview {
  word-break: break-all;
  white-space: pre;
  color: hsl(230, 1%, 56%);
}
.preview-pane .results-view .match-row .preview {
  color: hsl(230, 1%, 14%);
}
.preview-pane .results-view .match-row.selected .preview {
  color: hsl(230, 1%, 2%);
}
.preview-pane .results-view .selected .highlight-info {
  box-shadow: inset 0 0 1px hsl(0, 0%, 100%);
}
.preview-pane .results-view .selected .highlight-error {
  box-shadow: inset 0 0 1px hsl(5, 72%, 81%);
}
.preview-pane .results-view .selected .highlight-success {
  box-shadow: inset 0 0 1px hsl(132, 52%, 73%);
}
.find-container atom-text-editor .syntax--regexp .syntax--escape,
.replace-container atom-text-editor .syntax--regexp .syntax--escape {
  color: hsl(208, 100%, 54%);
}
.find-container atom-text-editor .syntax--regexp .syntax--range,
.replace-container atom-text-editor .syntax--regexp .syntax--range,
.find-container atom-text-editor .syntax--regexp .syntax--character-class,
.replace-container atom-text-editor .syntax--regexp .syntax--character-class,
.find-container atom-text-editor .syntax--regexp .syntax--wildcard,
.replace-container atom-text-editor .syntax--regexp .syntax--wildcard {
  color: hsl(132, 60%, 44%);
}
.find-container atom-text-editor .syntax--regexp .syntax--wildcard,
.replace-container atom-text-editor .syntax--regexp .syntax--wildcard {
  font-weight: bold;
}
.find-container atom-text-editor .syntax--regexp .syntax--set,
.replace-container atom-text-editor .syntax--regexp .syntax--set {
  color: inherit;
}
.find-container atom-text-editor .syntax--regexp .syntax--keyword,
.replace-container atom-text-editor .syntax--regexp .syntax--keyword,
.find-container atom-text-editor .syntax--regexp .syntax--punctuation,
.replace-container atom-text-editor .syntax--regexp .syntax--punctuation {
  color: hsl(0, 90%, 56%);
  font-weight: normal;
}
.find-container atom-text-editor .syntax--regexp .syntax--replacement.syntax--variable,
.replace-container atom-text-editor .syntax--regexp .syntax--replacement.syntax--variable {
  color: hsl(37, 90%, 44%);
}
